import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';

export default function EditNote() {

    const navigate = useNavigate();
    const { id } = useParams();

    const [note, setNote] = useState({
        date: "",
        task: "",
        status: ""
    });

    const { date, task, status } = note;

    const onInputChange = (e) => {
        setNote({ ...note, [e.target.name]: e.target.value });
    };

    useEffect(() => {
        if (!id) {
            alert("Note ID is missing in URL.");
            return;
        }
        loadNote();
    }, [id]);

    const onSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8080/note/${id}`, note);
            navigate("/");
        } catch (err) {
            alert("Update failed. Please check backend API.");
            console.error(err);
        }
    };

    const loadNote = async () => {
        try {
            const result = await axios.get(`http://localhost:8080/note/${id}`);
            setNote(result.data);
        } catch (error) {
            console.error("Error loading note:", error.response?.data || error.message);
            alert("Error loading note. Please check if the ID is valid.");
        }
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                    <h2 className="text-center m-4">Edit your Note</h2>
                    <form onSubmit={onSubmit}>
                        <div className="mb-3">
                            <label htmlFor='date' className='form-label'>Due Date</label>
                            <input
                                type="text"
                                className="form-control"
                                name="date"
                                value={date}
                                onChange={onInputChange}
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor='task' className='form-label'>Task</label>
                            <input
                                type="text"
                                className="form-control"
                                name="task"
                                value={task}
                                onChange={onInputChange}
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor='status' className='form-label'>Status</label>
                            <input
                                type="text"
                                className="form-control"
                                name="status"
                                value={status}
                                onChange={onInputChange}
                            />
                        </div>
                        <button type="submit" className="btn btn-outline-primary">Submit</button>
                        <Link className="btn btn-outline-danger mx-2" to="/">Cancel</Link>
                    </form>
                </div>
            </div>
        </div>
    );
}
